import './App.css';
import './MediaQuery.css';
import { useState,useEffect,useRef } from 'react';
import Simon from './gameboard.js'
import Header from './header.js'
import Controls from './controls.js';

//Customized functions
function autoTextFn(text,heading){
  text = text.split``
  var i = 0, arr = []
    let len = [...text].length
    let timer = setInterval(()=>{
      let take = text.shift(text[i])
       i++
       arr.push(take)
      heading.textContent = arr.join``
      // console.log(text)//sender
      // console.log(arr)//receiver
      // console.log(arr.length,len)//compare arr's length w/ original text length
      if(arr.length === len)clearInterval(timer)//clearInterval once both lengths are the same.
      },50)
}
function pregame_disabled(container){
  container.classList.add('no-pointer')
  setTimeout(()=>{
    container.classList.remove('no-pointer')
  },1000)
}
function playerDisabled(container,level,heading){ 
  let text = 'Your turn'
  level = Number(level)
  container.classList.add('no-pointer')
  setTimeout(()=>{
    container.classList.remove('no-pointer')
    autoTextFn(text,heading)
  },(level+1) * 1000)
}
function Level({level}){
return (
  <div>
    <h2 className="level">{level}</h2>
  </div>
)
}
function App() {
  let colors = ['yellow','green','red','purple']
  let shadows = ['#8B8000','#013220', '#8B0000','#301934']
  //HTML References
  let headingRef = useRef();
  let tilesRef = useRef();
  let controls_wrapperRef = useRef();
  let appRef = useRef();

  //states

const [sequence,setSequence] = useState([])
const [nextSequence, setNextSequence] = useState([])
const [level, setLevel] = useState(0)
const [playing, setPlaying] = useState(false) //state is for starting game
  //useEffects
  let c = 0;
  useEffect(()=>{
    if(playing){
      setLevel(lev => lev + 1)
    }
  },[playing])
//____________________________________________
//Events
const startGame = (e) => {
  setPlaying(true)
  let tiles = [...tilesRef.current.children]
    tiles.forEach(tile=>{
    tile.classList.remove('deactivated-default')
    tile.classList.add('deactivated-start')
  })
  nextRound()
  // setTimeout(()=>{
  //   nextRound()
  // },2000)
  // setTimeout(()=>{
  //   nextRound()
  // },4000) //Feel free to uncomment this setTimeout() function(fn)
  //Both timeouts begin as soon as you fire the "Play" button.
  //Purpose: To test that nextRound() fn will promise to increment the level's state by 1.

  var controls = controls_wrapperRef.current
      let played = e.target
      played.classList.add('disabled')
      controls.style = 'justify-content:center;'
      autoTextFn('Wait for Computer',headingRef.current)
}
const randomColor = () => { 
  return colors[Math.floor(Math.random()*4)]
}
//nextRound
const nextRound = () => {
  setPlaying(false)
  setLevel(lev => lev + 1)
  setNextSequence(next => next = randomColor())
  setSequence(seq => [...seq,nextSequence])
  console.log(sequence)
  console.log(randomColor())
}










//_____________________________________________

return (
    <div className="App" ref={appRef}>
      <span className="material-symbols-outlined pc-bg">laptop_mac</span>
      <Header {...{headingRef}}/>
      <Simon {...{colors,shadows,tilesRef}}/>
      <Controls {...{controls_wrapperRef,startGame}}/>
      <Level {...{level}}/>
    </div>
  );
}



export default App;
