import './App.css'
// import { useState, useEffect, useRef } from 'react';
import { useEffect,React } from 'react'


export default function Gameboard({tiles,tilesRef, myTileRef,human,comp,setComp,index}) {
  function clickFn(e){
    console.log(e.target.id)
    
    }
  return (
    <>
      <div id="gameboard-container" ref={tilesRef}>
        {tiles.map((tile)=>(
          <div className="tile deactivated-default" onClick={clickFn} key={tile.id} id={tile.color} ref={myTileRef} style={{backgroundColor:`${tile.color}`}}>
          </div>
        ))}
      </div>
    </>
  )
}
