import './App.css'
// import { useState, useEffect, useRef } from 'react';
import { useEffect,React } from 'react'


export default function Gameboard({tiles,tilesRef, myTileRef,comp,setComp,index,setIndex,playRound,level,human, setHuman}) {
  
  function clickFn(e){
    setHuman([...human,e.target.id])
    setIndex(index+1)
    console.log(index)
    console.log(human)
    console.log(comp)
    if(comp.length-2 === human.length){
      console.log('length passes')
    }
    
    }
  return (
    <>
      <div id="gameboard-container" ref={tilesRef}>
        {tiles.map((tile)=>(
          <div className="tile deactivated-default" onClick={clickFn} key={tile.id} id={tile.color} ref={myTileRef} style={{backgroundColor:`${tile.color}`}}>
          </div>
        ))}
      </div>
    </>
  )
}
