import './App.css'
// import { useState, useEffect, useRef } from 'react';
import { useEffect,React } from 'react'


export default function Gameboard({tiles,tilesRef, myTileRef,level}) {
useEffect(()=>{
  if(level > 3 && level < 5){
    tiles.forEach((tile,i)=>tile.style=`background-color: ${tile.alternate}`)
  }
  else{
    tiles.forEach((tile,i)=>tile.style=`background-color: ${tile.color}`)
  }
},[level])
  return (
    <>
      <div id="gameboard-container" ref={tilesRef}>
        {tiles.map((tile)=>(
          <div className="tile deactivated-default" key={tile.id} id={tile.color} ref={myTileRef} style={{backgroundColor:`${tile.color}`}}>
          </div>
        ))}
      </div>
    </>
  )
}
