import './App.css'
// import { useState, useEffect, useRef } from 'react';
import { useEffect,React } from 'react'

let arr = []
export default function Gameboard({tiles,tilesRef, myTileRef,comp,setComp,index,setIndex,playRound,human,subRef,autoTextFn,setGameStarted,setLevel,playRef,controlRef}) {
  function clickFn(e){
    arr.push(e.target.id)
    setIndex(index+1)
    console.log(index)
    console.log(arr)
    console.log(arr[index])
    console.log(comp)
    console.log(comp[index])

    if(arr[index]!==comp[index]){
      gameOver('game over')
    }
    if(arr[index]===comp[index] && arr.length===comp.length-1){
      arr=[]
      setIndex(0)
      playRound()
      }
    }
  function gameOver(txt){
    autoTextFn(txt,subRef.current)
    setIndex(0)
    playRef.current.classList.remove('disabled')
    controlRef.current.classList.remove('centered')
    controlRef.current.classList.add('space-evenly')
    setDisabled(true)

    setLevel(0)
    setTimeout(()=>{
      setGameStarted(false)
      arr = []
      setComp([])
    },1500)
    
  }
  return (
    <>
      <div id="gameboard-container" ref={tilesRef}>
        {tiles.map((tile)=>(
          <div className="tile deactivated-default" onClick={clickFn} key={tile.id} id={tile.color} ref={myTileRef} style={{backgroundColor:`${tile.color}`}}>
          </div>
        ))}
      </div>
    </>
  )
}
