import './App.css';
// import './MediaQuery.css';
import { useState, useEffect, useRef } from 'react';
import Header from './header.js'
import Controls from './controls.js';
import Gameboard from './gameboard.js'
//dataset
let tiles = [{
  id: 1,
  color: 'red',
  alternate: 'maroon',
  sound: null
},
{
  id: 2,
  color: 'green',
  alternate: '#029356',
  sound: null
},
{
  id: 3,
  color: 'blue',
  alternate: '#0073e6',
  sound: null
},
{
  id: 4,
  color: 'yellow',
  alternate: '#606ff3',
  sound: null
}]
function Level({ level }) {
  return (
    <div>
      <h2 className="level">{level}</h2>
    </div>
  )
}
function App() {
  //HTML References
  const tilesRef = useRef()
  const resetRef = useRef()
  const controlRef = useRef()
  const myTileRef = useRef()
  const headingRef = useRef()
  const subRef = useRef()
  const playRef = useRef()
  let start_count = 750
  //Customized functions
 function autoTextFn(text, heading) {
    text = text.split``
    var i = 0, arr = []
    let len = [...text].length
    let timer = setInterval(() => {
      let take = text.shift(text[i])
      i++
      arr.push(take)
      heading.textContent = arr.join``
      // console.log(text)//sender
      // console.log(arr)//receiver
      // console.log(arr.length,len)//compare arr's length w/ original text length
      if (arr.length === len) clearInterval(timer)//clearInterval once both lengths are the same.
    }, 35)
  }

  //states
  const [level, setLevel] = useState(0)
  const [disabled, setDisabled] = useState(false)
  const [color, setColor] = useState(tiles.map((_,i)=>_.color))
  const [gameStarted, setGameStarted] = useState(false)
  const [comp, setComp] = useState([])
  const [comp_speed, setComp_speed] = useState(start_count)
  const [comp_tile_delay, setComp_tile_delay] = useState(comp_speed/2)
  const [index, setIndex] = useState(0)
  //useEffects


  useEffect(()=>{
   let lev = document.querySelector('.level')
   let winHeight = window.innerHeight
   let winWidth = window.innerWidth
   lev.style=`top:${winHeight*.5}%;right:${winWidth*.5}%;`
  },[])
  useEffect(()=>{//Modify computer_speed
    if(level>0&&level%3==0){
      //computer_speed increases my 150ms every 4 rounds.
      //computer's tap delay = If level < 9 return (computer speed / 2), else 225 (minimum delay speed)
      setComp_speed(count => count - 150)
      setComp_tile_delay(delay => level < 9 ? delay - 75 : 225)
    }
    else{
      setComp_speed(comp_speed)
    }
  },[level])
  useEffect(()=>{ 
  if(gameStarted){//Once the game starts, the tiles look darker.
    subRef.current.textContent = ''
    tilesRef.current.classList.remove('deactivated-ready')
    tilesRef.current.classList.add('deactivated-ready')
    }
    else{
      console.log('gameStarted is false')    
      setComp([...comp,randomColor()])
      autoTextFn('Simon Says', headingRef.current)
      autoTextFn('Try to beat all 20 rounds!',subRef.current)
      tilesRef.current.classList.remove('deactivated-ready')
      tilesRef.current.classList.add('deactivated-default')

    }
  },[gameStarted])
  useEffect(() => {
      if(!disabled){
        tilesRef.current.classList.remove('no-pointer')
      }
      else {
        tilesRef.current.classList.add('no-pointer')
      }  
  }, [disabled,level])

  //____________________________________________
  //action creators
  const startGame = (e) => {
    gameInitialized(e)
    playRound()
}
  const gameInitialized = (e) => {
    setGameStarted(true)
    setDisabled(true)
    let play = e.target
    let controlWrapper = controlRef.current
    play.classList.add('disabled')
    controlWrapper.classList.remove('space-evenly')
    controlWrapper.classList.add('centered')
  }
  const playRound = () => {
    autoTextFn('Wait for Computer',subRef.current)
    setComp([...comp, randomColor()])
    setLevel(level + 1)
    activate(comp)
    humanTurn(level)
  }
  const humanTurn = (level) =>{
    setTimeout(()=>{
    setDisabled(false)
    autoTextFn('Your turn',subRef.current)

    },level > 1 ? (level+2*1000)+1500 : (level+2*1000))
  }
  const activate = (array) =>{
    array.forEach((tile,i) =>{
        setTimeout(()=>{
          blink(tile,i)
        },(i+1)*comp_speed)
    })
  }
  const blink = (col) =>{
    let elem = document.querySelector(`#${col}`)
    elem.classList.remove('deactivated-ready')
    elem.classList.remove('deactivated-default')
    setTimeout(()=>{
      elem.classList.add('deactivated-ready')
      elem.classList.add('deactivated-default')
      elem.classList.toggle('deactivated-ready')
    },comp_tile_delay)
  } 
  const randomColor = () => {
   let random =  color[Math.floor(Math.random()*color.length)]
   return random
  }



  //_____________________________________________

  return (
    <div className="App">
      <span className="material-symbols-outlined pc-bg">laptop_mac</span>
      <Header {...{ headingRef, subRef }} />
      <Gameboard {...{ tiles,start_count,comp_speed,setComp_speed,setComp_tile_delay,tilesRef, myTileRef,comp,setComp,index,setIndex,level,playRound,level,subRef,autoTextFn,setGameStarted,setLevel, playRef,controlRef,setDisabled}} />
      <Controls {...{ startGame, resetRef, controlRef,playRef }} />
      <Level {...{ level }} />
    </div>
  );
}

export default App;
