import './App.css'
// import { useState, useEffect, useRef } from 'react';
import { useEffect,React } from 'react'

let human = []
export default function Gameboard({pcbgRef,tiles,tilesRef,start_count,comp_speed,setComp_speed,setComp_tile_delay,myTileRef,comp,setComp,index,setIndex,playRound,subRef,autoTextFn,setGameStarted,setLevel,playRef,controlRef,setDisabled,level}) {
  useEffect(()=>{
    if(level < 1) {
      setDisabled(true)
    }
    
  },[level])
  useEffect(()=>{
    let wid = pcbgRef.current.clientWidth
    let hei = pcbgRef.current.clientHeight
    myTileRef.current.style = `height:${hei/2}px`
    
  },[])
  function clickFn(e){
    human.push(e.target.id)
    setIndex(index+1)
    console.log(index)
    console.log(human)
    console.log(human[index])
    console.log(comp)
    console.log(comp[index])

    if(human[index]!==comp[index]){
      gameOver('game over')
      setDisabled(true)
    }
    if(human[index]===comp[index] && human.length===comp.length-1){
      human=[]
      setIndex(0)
      setDisabled(true)
      autoTextFn(`You passed level ${level}!`,subRef.current)
      setTimeout(()=>{
        playRound()
      },1500)
      }
    }
  function gameOver(txt){
    start_count=750 
    autoTextFn(txt,subRef.current)
    setIndex(0)
    playRef.current.classList.remove('disabled')
    controlRef.current.classList.remove('centered')
    controlRef.current.classList.add('space-evenly')
    setDisabled(d => d = true)
    setComp_speed(start_count)
    setComp_tile_delay(comp_speed/2)
    setLevel(0)
    setComp([])
    human = []
    setTimeout(()=>{
      setGameStarted(false)
    },1500)
    
  }
  return (
    <>
      <div id="gameboard-container" ref={tilesRef}>
        {tiles.map((tile)=>(
          <div className="tile deactivated-default" onClick={clickFn} key={tile.id} id={tile.color} ref={myTileRef} style={{backgroundColor:`${tile.color}`}}>
          </div>
        ))}
      </div>
    </>
  )
}
