import './App.css';
import './MediaQuery.css';
import { useState,useEffect,useRef } from 'react';
import Header from './header.js'
import Controls from './controls.js';
import Gameboard from './gameboard.js'
//dataset
let tiles = [{
  id:'red',
  color:'red',
  alternate:'maroon',
  sound: null
},
{
  id:'green',
  color:'green',
  alternate:'#029356',
  sound: null
},
{
  id:'blue',
  color:'blue',
  alternate:'#0073e6',
  sound: null
},
{
  id:'yellow',
  color:'yellow',
  alternate:'#606ff3',
  sound: null
}]
function Level({level}){
return (
  <div>
    <h2 className="level">{level}</h2>
  </div>
)
}

function App() {
//HTML References
const tilesRef = useRef()
const resetRef = useRef()
const controlRef = useRef()
const myTileRef = useRef()
const headingRef = useRef()
const subRef = useRef()
//Customized functions
function autoTextFn(text,heading){
  text = text.split``
  var i = 0, arr = []
    let len = [...text].length
    let timer = setInterval(()=>{
      let take = text.shift(text[i])
       i++
       arr.push(take)
      heading.textContent = arr.join``
      // console.log(text)//sender
      // console.log(arr)//receiver
      // console.log(arr.length,len)//compare arr's length w/ original text length
      if(arr.length === len)clearInterval(timer)//clearInterval once both lengths are the same.
      },35)
}
function humanDisabled(lvl){
  setDisabled(true)
  setTimeout(()=>{
    setDisabled(false)
    autoTextFn('Your turn', headingRef.current)
  },(lvl+1)*1750)
}
//states
const [level, setLevel] = useState(0)
const [disabled, setDisabled] = useState(false)
const [sequence,setSequence] = useState([])
const [myArr, setMyArr] = useState([])
const [compare,setCompare] = useState([])//Comparing lengths b/w sequence & myArr 
const [index,setIndex] = useState(0)
//useEffects
useEffect(()=>{
let head = headingRef.current.textContent;
let tilesArr = [...tilesRef.current.children]
if(!disabled || head === 'Your turn'){
  tilesArr.forEach(tile =>{
    tile.addEventListener('click', e=>{
      let col = e.target.id
      if(col) handleClick(col)
    })
  })
}
if(disabled || level  < 1){
  let subText = 'Try to make it to 20 rounds!'
  autoTextFn('Simon Says', headingRef.current)
  let time = () => autoTextFn(subText, subRef.current);
  if(level < 1){
    let go_away_sub = 'opacity:0;font-size:0;transition:.5s ease;'
    time()
    setTimeout(()=> subRef.current.style=go_away_sub,2750)
  }
  tilesRef.current.classList.add('no-pointer')
}
else{
  tilesRef.current.classList.remove('no-pointer')
}
},[disabled])
useEffect(()=>{
  if(level === 0){
    setSequence([...sequence,randomColor()])
  }
},[])
//____________________________________________
//action creators
const gameInitialized = (e) => {
  subRef.current.textContent = ''
  tilesRef.current.classList.add('deactivated-ready')
  tilesRef.current.classList.remove('deactivated-default')
  let play = e.target
  let controlWrapper = controlRef.current
  // play.classList.add('disabled')
  // controlWrapper.classList.toggle('centered')
}
const startGame = (e) => {
  setDisabled(true)
  gameInitialized(e)
  nextRound()
}
const randomColor = () => {
  let arr = [];
    let random;
    tiles.forEach(tile =>{
      arr.push(tile.color)
      random = arr[Math.floor(Math.random() * 4)]
    })
    return random
}
const activate = (t) => {
  var tile = document.getElementById(`${t}`)
  tile.classList.remove('deactivated-default')
  setTimeout(()=>{
    tile.classList.add('deactivated-default')
  },300)
}
const playRound = (arr) => {
  arr.forEach((tile,i) => {
    setTimeout(()=>{
      activate(tile)
    },(i+1)*1000)
  })
}
let c  = 0;
const handleClick = (color) => {
setIndex([...sequence].lastIndexOf(color)) //index to compare
setCompare([...sequence])
setMyArr([...myArr,color])
setSequence([...sequence,randomColor()])
if(index==-1){
  resetGame('Game Over!')
  return
}
}
const resetGame = (text) => {
  autoTextFn(text, headingRef.current)
  setLevel(0)
  setSequence([])
  setMyArr([])
  setCompare([])
  setIndex(0)
  setDisabled(true)
  setTimeout(()=>{
    autoTextFn('Simon Says', headingRef.current)
  },3000)
}

const nextRound = () => {
// setSequence([...sequence,randomColor()])
autoTextFn('Simon\'s turn', headingRef.current)
setLevel(level + 1)
playRound(sequence)
humanDisabled(level)
// console.log('sequence-1: '+sequence)
// console.log('sequence-2: '+myArr) //uncomment to see how clicking a tile will push it's color into myArr state
console.log(sequence)
// console.log(myArr[index])
// console.log(compare[index])
console.log(index)

}


//_____________________________________________

return (
    <div className="App">
      <span className="material-symbols-outlined pc-bg">laptop_mac</span>
      <Header {...{headingRef,subRef}}/>
      <Gameboard {...{tiles,tilesRef,myTileRef}}/>
      <Controls {...{startGame,resetRef,controlRef}}/>
      <Level {...{level}}/>
    </div>
  );
}



export default App;
